import StylePanel from '../components/StylePanel.vue';
export default {
  name: 'EditorField',
  components: {
    StylePanel
  },

  data() {
    return {
      insert: true
    };
  },

  methods: {
    moveCaret(win, charCount) {
      var sel;

      if (win.getSelection) {
        sel = win.getSelection();

        if (sel.rangeCount > 0) {
          var textNode = sel.focusNode;
          var newOffset = sel.focusOffset + charCount;
          sel.collapse(textNode, Math.min(textNode.length, newOffset));
        }
      }
    },

    saveDocument() {
      var preHtml = "";
      var postHtml = "";
      var html = preHtml + this.$refs.editorfield.innerHTML + postHtml;
      var blob = new Blob(['\ufeff', html], {
        type: 'application/msword'
      }); // Specify link url

      var url = 'data:application/vnd.ms-word;charset=utf-8,' + encodeURIComponent(html); // Specify file name

      var filename = prompt("Please enter file name") + '.doc'; // Create download link element

      var downloadLink = document.createElement("a");
      document.body.appendChild(downloadLink);

      if (navigator.msSaveOrOpenBlob) {
        navigator.msSaveOrOpenBlob(blob, filename);
      } else {
        // Create a link to the file
        downloadLink.href = url; // Setting the file name

        downloadLink.download = filename; //triggering the function

        downloadLink.click();
      }

      document.body.removeChild(downloadLink);
    },

    keyPress(e) {
      if (this.insert == true) {
        switch (e.keyCode) {
          case 73:
            e.preventDefault();
            alert("Insert mode");
            this.insert = false;
            break;

          case 72:
            e.preventDefault();
            this.moveCaret(window, -1);
            break;

          case 76:
            e.preventDefault();
            this.moveCaret(window, +1);
            break;

          case 88:
            e.preventDefault();
            this.format('forwardDelete');
            break;

          case 9:
            e.preventDefault();
            let command = prompt("Write the command here:");

            switch (command) {
              case "bold":
                this.format('bold');
                break;

              case "italic":
                this.format('italic');
                break;

              case "list":
                this.format('insertunorderedlist');
                break;

              case "textsize":
                let size = prompt("Write the text size here (1-7):");

                if (size < 1 || size > 7) {
                  alert("This is not an option!");
                } else {
                  this.textSize(size);
                }

                break;

              case "font":
                let fontName = prompt("Write the font here (Arial, Helvetica, Times New Roman, Courier New, Archivo Narrow, Roboto):");

                if (fontName == "Arial" || fontName == "Helvetica" || fontName == "Times New Roman" || fontName == "Courier New" || fontName == "Archivo Narrow" || fontName == "Roboto") {
                  this.font(fontName);
                } else {
                  alert("This is not an option!");
                }

                break;

              case "listol":
                this.format('insertorderedlist');
                break;

              case "textcolor":
                let colorname = prompt("Write the color here (Yellow, Green, Blue, Violet, Red, Orange, Black, Gray, Brown, White):");
                this.colorSwitch(colorname, true);
                break;

              case "backcolor":
                let backcolorname = prompt("Write the color here (Yellow, Green, Blue, Violet, Red, Orange, Black, Gray, Brown, White):");
                this.colorSwitch(backcolorname, false);
                break;

              case "alignleft":
                this.format('justifyLeft');
                break;

              case "aligncenter":
                this.format('justifyCenter');
                break;

              case "alignright":
                this.format('justifyRight');
                break;

              case "subscript":
                this.format('subscript');
                break;

              case "superscript":
                this.format('superscript');
                break;

              case "link":
                this.link();
                break;

              case "unlink":
                this.format('unlink');
                break;

              case "underline":
                this.format('underline');
                break;

              case "wq":
                this.saveDocument();
                break;

              default:
                alert("This is not a command!");
            }

          case 37:
            break;

          case 38:
            break;

          case 39:
            break;

          case 40:
            break;

          default:
            e.preventDefault();
        }
      } else if (this.insert == false) {
        if (e.keyCode == 27) {
          alert("Normal mode");
          this.insert = true;
        }
      }
    },

    format(command, value) {
      document.execCommand(command, false, value);
    },

    textSize(size) {
      document.execCommand("fontSize", false, size);
    },

    font(fontName) {
      document.execCommand("fontName", false, fontName);
    },

    textColor(textcolor) {
      document.execCommand("foreColor", false, textcolor);
    },

    backColor(backcolor) {
      document.execCommand("backColor", false, backcolor);
    },

    link() {
      let url = prompt("Write the URL here:");
      this.format('createLink', url);
    },

    colorSwitch(colorname, textcolor) {
      let colorcode;

      switch (colorname) {
        case "Yellow":
          colorcode = "ffff00";
          break;

        case "Green":
          colorcode = "#008000";
          break;

        case "Blue":
          colorcode = "#0000ff";
          break;

        case "Violet":
          colorcode = "#ee82ee";
          break;

        case "Red":
          colorcode = "#ff0000";
          break;

        case "Orange":
          colorcode = "#ffa500";
          break;

        case "Black":
          colorcode = "#000000";
          break;

        case "Gray":
          colorcode = "#808080";
          break;

        case "Brown":
          colorcode = "#8b4513";
          break;

        case "White":
          colorcode = "#ffffff";
          break;

        default:
          alert("This is not an option!");
      }

      if (textcolor == true) {
        this.textColor(colorcode);
      } else if (textcolor == false) {
        this.backColor(colorcode);
      }
    }

  }
};
