import StylePanel from '../components/StylePanel.vue';
export default {
  name: 'EditorField',
  components: {
    StylePanel
  },

  data() {
    return {
      insert: true
    };
  },

  methods: {
    keyPress(e) {
      if (this.insert == true) {
        if (e.keyCode == 73) {
          e.preventDefault();
          alert("Insert mode");
          this.insert = false;
        } else if (e.keyCode == 72) {
          e.preventDefault();
          e.target.setSelectionRange(e.target.selectionStart - 1, e.target.selectionStart - 1);
        } else if (e.keyCode == 74) {
          e.preventDefault();
        } else if (e.keyCode == 76) {
          e.preventDefault();
          e.target.setSelectionRange(e.target.selectionStart + 1, e.target.selectionStart + 1);
        } else if (e.keyCode == 37) {} else if (e.keyCode == 38) {} else if (e.keyCode == 39) {} else if (e.keyCode == 40) {} else {
          e.preventDefault();
        }
      } else if (this.insert == false) {
        if (e.keyCode == 27) {
          alert("Normal mode");
          this.insert = true;
        }
      }
    },

    format(command) {
      document.execCommand(command, false);
    }

  }
};
